
public class Swap {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
    int a =10;
    int b = 20;
    
    System.out.println("before swap"+ a +"and "+b);
    int temp = a;
    a=b;
    b = temp;
    System.out.println("after swap"+a +"and"+b);
    
    
	}

}
